import React, { useEffect } from 'react'

import { useDispatch, useSelector } from 'react-redux';

import { Row, Col, Form, Button, Card, Image, ListGroup, ListGroupItem } from 'react-bootstrap';
import { addToCart } from '../actions/cartAction';
import { useParams, useLocation } from 'react-router-dom';
const CartScreen = ({match, history}) => {
  const location = useLocation();
  const {id} = useParams();
  const productId = id;
  const qyt = location.search ? Number(location.search.split("=")[1]):1;
  const dispatch = useDispatch();

  useEffect(()=>{
    if(productId){
      dispatch(addToCart(productId, qyt));
    }
  },[dispatch, productId, qyt]);

  const cart = useSelector((state)=>state.cart);
  const {cartItems}  = cart;
  console.log(cartItems);
  return (
    <div>
      <h1>CART</h1>
    </div>
  )
}

export default CartScreen;
